<?php

namespace Nfq\WeDriveBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Nfq\UserBundle\Entity\User;

/**
 * TripRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TripRepository extends EntityRepository
{


    public function getUserTrips(User $user)
    {
        $em = $this->getEntityManager();

        $parameters = array(
            'userId' => $user->getId(),
            'departureTime' => date('Y-m-d H:i:s')
        );

        $query = $em->createQuery(
            "
                            SELECT t
                            FROM Nfq\WeDriveBundle\Entity\Trip t
                            JOIN t.route r
                            WHERE r.user = :userId
                            AND t.departureTime > :departureTime
                            ORDER BY t.departureTime ASC
                        "
        )->setParameters($parameters);

        $trips = $query->getResult();

        if (!$trips) {
//            throw $this->createNotFoundException(
//                'No trips found'
//            );
        }

        return $trips;
    }
    /**
     * getOtherTrips
     *
     * @param User
     * @return array
     */
    public function getOtherTrips(User $user)
    {


        $em = $this->getEntityManager();

        $query = $em->createQuery(
            "
                            SELECT t
                            FROM Nfq\WeDriveBundle\Entity\Trip t
                            JOIN t.route r
                            JOIN r.user u
                            WHERE u.username != :username
                            ORDER BY t.departureTime
                        "
        )->setParameter('username', $user->getUsername());

        $trips = $query->getResult();
        if (!$trips) {
//            throw $this->createNotFoundException(
//                'No trips found'
//            );
        }

       return $trips;
    }

    /**
     * getAcceptedPassengersCount
     *
     * @param Trip trip
     * @return Integer
     */
    public function getAcceptedPassengersCount($trip)
    {
        $em = $this->getEntityManager();

        $query = $em->createQuery(
            "
                            SELECT count(p)
                            FROM Nfq\WeDriveBundle\Entity\Passenger p
                            JOIN p.trip t
                            WHERE t.id = :tripId and p.accepted = 2
                        "
        )->setParameter('tripId', $trip->getId());

        $pCount = $query->getSingleScalarResult();

        return $pCount;
    }
}
